import { useState } from 'react'

const Display = (props) => {
  return (
    <div>{props.counter}</div>
  );
}

const Button = (props) => {
  return (
    <button onClick={props.onClick}>
      {props.text}
    </button>
  )
}

const App = () => {
  const [ counter, setCounter ] = useState(0);

  const handleClick = () => {
    setCounter(counter + 1);
  }

  const reset = () => {
    setCounter(0);
  }

  return (
    <div>
      <Display counter={counter}/>
      <button onClick={handleClick}>
        plus
      </button>
      <div>
        <button onClick={reset}>
          reset
        </button>
      </div>
    </div>
  )
}

export default App
